import React from 'react';

const BotSpecs = ({ bot, handleEnlist, handleGoBack }) => {
  const { name, health, damage, armor, bot_class, image } = bot;

  return (
    <div className="bot-specs-container">
      <img src={image} alt={name} className="bot-image" />
      <h2>{name}</h2>
      <ul>
        <li>Health: {health}</li>
        <li>Damage: {damage}</li>
        <li>Armor: {armor}</li>
        <li>Class: {bot_class}</li>
      </ul>
      <div className="button-container">
        <button onClick={handleEnlist}>Enlist</button>
        <button onClick={handleGoBack}>Back to Bots</button>
      </div>
    </div>
  );
};

export default BotSpecs;
