import React, { useState } from "react";
import BotCard from "./BotCard";
import BotSpecs from "./BotSpecs";

const BotsCollection = ({ bots, handleEnlistBot }) => {
  const [selectedBot, setSelectedBot] = useState(null);

  const handleSelectBot = (bot) => {
    setSelectedBot(bot);
  };

  const handleBackToCollection = () => {
    setSelectedBot(null);
  };

  return (
    <div>
      {selectedBot ? (
        <BotSpecs bot={selectedBot} handleBackToCollection={handleBackToCollection} handleEnlistBot={handleEnlistBot} />
      ) : (
        <div>
          <h1>Bots Collection</h1>
          {bots.map((bot) => (
            <BotCard key={bot.id} bot={bot} handleSelectBot={handleSelectBot} />
          ))}
        </div>
      )}
    </div>
  );
};

export default BotsCollection;
