import React from 'react';
import BotCard from './BotCard';
;

function YourBotArmy({ army, onDischarge, onDischargeForever }) {
  const handleDischarge = (bot) => {
    onDischarge(bot);
  };

  const handleDischargeForever = (bot) => {
    onDischargeForever(bot);
  };

  return (
    <div className="your-bot-army">
      <h2>Your Bot Army</h2>
      <div className="bot-list">
        {army.map(bot => (
          <BotCard key={bot.id} bot={bot} onDischarge={handleDischarge} onDischargeForever={handleDischargeForever} />
        ))}
      </div>
    </div>
  );
}

export default YourBotArmy;
